import React from "react";
import ImageHero from "../assets/images/hero.svg";
import HeroFrame from "../assets/images/hero-frame.svg";
import IconCities from "../assets/images/icons/ic_cities.svg";
import IconTraveler from "../assets/images/icons/ic_traveler.svg";
import IconTreasure from "../assets/images/icons/ic_treasure.svg";

export default function Hero(props) {
  function showMostPicked() {
    window.scrollTo({
      top: props.refMostPicked?.current.offsetTop - 30,
      behavior: "smooth",
    });
  }
  return (
    <section className="container px-36">
      <div className="flex flex-row items-center contents-center">
        <div className="flex flex-col  contents-center pr-5">
          <h1 className="font-bold text-4xl text-dark-blue">
            Forget Busy Work,
            <br />
            Start Next Vacation
          </h1>
          <p className="font-light text-gray-400 w-2/4 mb-5">
            We provide what you need to enjoy your holiday with family. Time to
            make another memorable moments.
          </p>
          <button className="bg-blue-700">Show Me Now</button>
          <div className="mt-5 flex flex-row">
            <div className="flex flex-col mr-12">
              <img
                width={36}
                height={36}
                src={IconTraveler}
                alt={`${props.data.travelers} Travelers`}
              />
              <h6 className="mt-3">
                {props.data.travelers}{" "}
                <span className="text-gray-500 font-light"> travellers</span>
              </h6>
            </div>
            <div className="flex flex-col mr-12">
              <img
                width={36}
                height={36}
                src={IconTreasure}
                alt={`${props.data.treasures} Treasures`}
              />
              <h6 className="mt-3">
                {props.data.treasures}{" "}
                <span className="text-gray-500 font-light"> treasures</span>
              </h6>
            </div>
            <div className="flex flex-col">
              <img
                width={36}
                height={36}
                src={IconCities}
                alt={`${props.data.cities} Cities`}
              />
              <h6 className="mt-3">
                {props.data.cities}{" "}
                <span className="text-gray-500 font-light"> cities</span>
              </h6>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
}
