import React from "react";
import { Link } from "react-router-dom";

export default function Categories({ data }) {
  return data.map((category, index1) => {
    return (
      <section className="px-36" key={`category-${index1}`}>
        <h4 className="text-2xl mb-5">{category.name}</h4>
        <div className="grid grid-rows gap-7">
          {category?.items.length === 0 ? (
            <div>There is no property in this category</div>
          ) : (
            category?.items.map((item, index2) => {
              return (
                <div
                  className="group bg-crimson-500 min-h-[180px] w-[263px] row-span-2 relative overflow-hidden rounded-2xl"
                  key={`category-${index1}-item-${index2}`}
                >
                  <div
                    className="inline-flex items-center justify-center rounded-tr-2xl 
              rounded-bl-2xl bg-magenta text-white font-medium z-10 w-[180px] h-[40px] absolute right-0"
                  >
                    {item.isPopular && (
                      <div className="text-white font-medium">
                        Popular{" "}
                        <span className="text-white font-light">choice</span>
                      </div>
                    )}
                    <div className="object-cover relative group-hover:scale-110 ease-in-out duration-300">
                      <img
                        src={item.imageUrl}
                        alt={item.name}
                        className="absolute z-0"
                      />
                    </div>
                    <div className="absolute  z-10 group-hover:bottom-6 ease-in-out duration-300">
                      <Link className="text-white font-regular text-xl">
                        {item.name} <br />
                        <span className="text-white font-light text-[15px]">
                          {item.city}, {item.country}{" "}
                        </span>
                      </Link>
                    </div>
                  </div>
                </div>
              );
            })
          )}
        </div>
      </section>
    );
  });
}
